const styleContent = {
    width: '100%',
    height: '100%',
    display: 'flex',
    flex: '1 1 0',
    alignItems: 'center',
    justifyContent: 'center'
}

const Layout = (Page) => {
    return () => (
        <div>
            <Page />
            <style global jsx>{`
                html {
                    height: 100%;
                }
                body {
                    display: flex;
                    flex: 1 1 100%;
                    justify-content: center;
                    align-items: center;
                    margin: 0;
                }
            `}</style>
        </div>
    )
}

export default Layout
