const Layout = (Page) => {
    return () => (
        <div className="content">
            <Page />
            <style global jsx>{`
                html {
                    height: 100%;
                }
                body {
                    display: flex;
                    flex: 1 1 100%;
                    height: 100%;
                    justify-content: center;
                    align-items: center;
                    margin: 0;
                    background-color: #5fffc1;
                    animation-name: changeBackgroundColor;
                    animation-duration: 6s;
                }

                .content {
                    height: 100%;
                }
                @keyframes changeBackgroundColor {
                    from { background-color: #5fffc1 }
                    to { background-color: #ddfdf1 }
                }
            `}</style>
        </div>
    )
}

export default Layout
